import React from 'react';

export default function subjects(props) {

    let answers = [];

    function putAllAnswersInArray() {
        answers.push(props.correct_answer);
        props.incorrect_answers.map((ele) => {
            return answers.push(ele);
        });
    }
    putAllAnswersInArray()

    let shuffleAnswers = answers.sort(()=> {
        return Math.random() - 0.5;
    });


    const allAnswers = shuffleAnswers.map((answer, index) => {
        return (<li key={index} onClick={handleClick}>{answer}</li>)
    });

    ////////////////////////////////////////////
    console.log(props.correct_answer);
    function handleClick(e) {
        // console.log(props.correct_answer);
        console.log(e.currentTarget.innerHTML);
        // if(e.currentTarget === props.correct_answer) {
        //     console.log('correct');
        // } else {
        //     console.log('wrong');
        // }
    }


    return (
        <div className='q-box'>
            <h3 className='que'>{props.question}</h3>
            <ul className='ans'>
                {allAnswers}
            </ul>
            <hr/>
        </div>
    )
}
