import React from 'react';
import './App.css';
import {nanoid} from 'nanoid';
import Begin from './components/Begin';
import Subjects from './components/Subjects';
// import axios from 'react-axios';
import Home from './components/Home'
import About from './components/About'
import Contact from './components/Contact'
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";


function App() {
  const [state, setState] = React.useState([]);

  React.useEffect(()=> {
    const fetchData = async ()=> {
      const res = await fetch('https://opentdb.com/api.php?amount=5&category=23&difficulty=medium&type=multiple');
      const data = await res.json();
      setState(data.results);
    }
    fetchData()
  }, []);

  const subjects = state.map(sub => {
    return (
      <Subjects 
        key={nanoid()}
        question = {sub.question}
        correct_answer = {sub.correct_answer}
        incorrect_answers = {sub.incorrect_answers}
      />
    )
  });


  return (
    // <div className="App">
    //   <Begin />
    //   <div className='qa-page'>
    //     {subjects}
    //     <button className='check-btn btn'>Check answers</button>
    //   </div>
      <Router>
        <ul>
          <li>
            <Link to={'/'}>Home</Link>
          </li>
          <li>
            <Link to={'/about'}>About</Link>
          </li>
          <li>
            <Link to={'/contact'}>Contact</Link>
          </li>
        </ul>
        <hr />

        <Routes>
          <Route exact path='/'>
            <Home />
          </Route>
          <Route exact path='/about'>
            <About />
          </Route>
          <Route exact path='/contact'>
            <Contact />
          </Route>
        </Routes>
      </Router>
    /* </div> */
  );
}

export default App;
