  //         <button className='btn check-btn'>Check Answers</button>
  //       </div>
  //     )


  return (


    <div className="App">
      <Routes>
        <Route path='/' element={<Begin />}></Route>
        <Route path='/subjects' element={questionElement}></Route>
      </Routes>
    </div>
  );
}

export default App;
