import React from 'react';

export default function subjects(props) {
    let answers = [];
    // answers.push(<li>{props.correct_answer}</li>);
    // const incorrect = props.incorrect_answers.map((ele, index)=> {
    //     return answers.push(<li key={index}>{ele}</li>)
    // });
    // // answers.push(incorrect);
    // console.log(answers);

    function putAllAnswersInArray() {
        answers.push(props.correct_answer);
        props.incorrect_answers.map((ele)=> {
            answers.push(ele);
        });
    }
    
    console.log(answers);


  return (
    <div className='q-box'>
        <h3 className='que'>{props.question}</h3>
        <ul className='ans'>
            <li>{props.correct_answer}</li>
            {answers}
        </ul>
        <hr/>
    </div>
  )
}
