import React from 'react';
import './Subjects.css';

export default function subjects(props) {

    let answers = [];

    function putAllAnswersInArray() {
        answers.push(props.correct_answer);
        props.incorrect_answers.map((ele) => {
            return answers.push(ele);
        });
    }
    putAllAnswersInArray()

    let shuffleAnswers = answers.sort(()=> {
        return Math.random() - 0.5;
    });


    const allAnswers = shuffleAnswers.map((answer, index) => {
        return (<li className='' key={index} onClick={selectAnswer}>{answer}</li>)
    });

    ////////////////////////////////////////////

    let selectedAnswerStyle = 'background: #D6DBF5; color: #293264;'
    let correctAnswerStyle = {background: '#94D7A2', color: '#293264',}
    let WrongAnswerStyle = {background: '#F8BCBC', color: '#293264',}

    let arr = [];
    arr.push(allAnswers)
    function selectAnswer(e) {
        // e.currentTarget.style = selectedAnswerStyle;
        arr.forEach(ar=> {
            ar.forEach(el=>{
            //     if(el.classList.contains('add')) {
            //         console.log("have");
            //     } else {
            //         console.log("not");
            //     }
            // console.log(el);
            })
            console.log(ar.className);
        })
    }





    return (
        <div className='q-box'>
            <h3 className='que'>{props.question}</h3>
            <ul className='ans'>
                {allAnswers}
            </ul>
            <hr/>
        </div>
    )
}
