import React from 'react';
// import './Question.css';
import {nanoid} from 'nanoid';
// import {ClassList} from 'react-classlist';


export default function Question(props) {
    // let answers = [];
    
    // function putAllAnswersInArray() {
    //     answers.push(props.correct_answer);
    //     props.incorrect_answers.map((ele) => {
    //         return answers.push(ele);
    //     });
    // }
    // putAllAnswersInArray()

    // let shuffleAnswers = answers.sort(()=> {
    //     return Math.random() - 0.5;
    // });
    
    // const allAnswers = shuffleAnswers.map((answer, index) => {
    //     return (<li key={index} onClick={selectAnswer}>{answer}</li>)
    // });
    
    // ////////////////////////////////////////////

    // // function handle() {
    //     // const li = ()=> {

    // // }

    // let selectedAnswerStyle = 'background: #D6DBF5; color: #293264;'
    // // let correctAnswerStyle = {background: '#94D7A2', color: '#293264',}
    // // let WrongAnswerStyle = {background: '#F8BCBC', color: '#293264',}

    // // const classes = new ClassList('add')


    // let arr = [];
    // arr.push(allAnswers)
    // function selectAnswer(e) {
    //     e.currentTarget.style = selectedAnswerStyle;
    //     // e.currentTarget.classList.remove('add');
    //     // arr.forEach(ele => {
    //     //     console.log(ele);
    //     // })
    // }


    let answers = props.q.answers;

    function handleClick(answer) {
        if(props.q.checked) {
            return 
        }
        props.handleClickAnswer(props.id, answer)
    }

    const answersElement = answers.map(answer => {
        let id = null;
        if(props.q.checked) {
            if(props.q.correct === answer) {
                id = 'correct';
            }
            else if(props.q.selected === answer) {
                id = 'incorrect';
            }
            else {
                id = 'not-selected'
            }
        }
        return (
            <li key={nanoid()} id={id} className={answer === props.q.selected ? 'answer selected' : 'answer'} onClick={() => handleClick(answer)}>{answer}</li>
        )
    });



    return (
        <div className='q-box'>
            <h3 className={'que'} >{props.q.question}</h3>
            <ul className='ans'>
                {/* <li>{answersElement}</li> */}
                {answersElement}
            </ul>
            <hr/>
        </div>
    )
}
